import classNames from 'classnames';

const DropdownMenu = {
  props: {
    tag: {
      type: String,
      default: "div"
    },
    dropup: {
      type: Boolean,
      default: false
    },
    right: {
      type: Boolean,
      default: false
    },
    dropright: {
      type: Boolean,
      default: false
    },
    dropleft: {
      type: Boolean,
      default: false
    },
    color: {
      type: String
    }
  },
  computed: {
    className() {
      return classNames(
        'dropdown-menu',
        'show',
        this.right ? 'dropdown-menu-right' : '',
        this.dropup ? 'dropup' : '',
        this.color ? 'dropdown-' + this.color : '',
        this.dropright ? 'dropright' : '',
        this.dropleft ? 'dropleft' : ''
      );
    }
  }
};

export default DropdownMenu;
export { DropdownMenu as mdbDropdownMenu };
